import React from 'react'
import { useState, useContext } from 'react';
import { ContextGlobal } from './utils/global.context';
import { Link } from 'react-router-dom';

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Navbar = () => {
  const { theme, setDark, setLight } =useContext(ContextGlobal);
  const isDarkMode = theme === "dark" || false;
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const changeTheme=()=>{
    if(isDarkMode){setLight();
      }
    else {setDark();}  };
  const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
          };
          
  
  return (
  <>
    <nav className={`${isDarkMode ? 'dark' : ''}`}>
      {/* Aqui deberan agregar los liks correspondientes a las rutas definidas */}
     <div className="wrapper"> 
      <Link className={`navbarLogo ${isDarkMode ? 'dark' : ''}`}to="/">
            DH Odonto
          </Link>
      <button
                className={`navbarMenuButton ${isMenuOpen ? 'active' : ''}`}
                          onClick={toggleMenu}
                                  >
                                            <span></span>
                                                      <span></span>
                                                                <span></span>
                                                                        </button>
     <div className={`navBarLink ${isDarkMode ? 'dark' : ''}  ${isMenuOpen ? 'navbarMenu' : ''}`}>
      <Link to="/">
            Home
         </Link>          
      <Link  to="/contact">
            Contact
         </Link>    

      <Link className={``} to="/Favs">
            Favs
        </Link>    

      {/* Deberan implementar ademas la logica para cambiar de Theme con el button */}
      <button style={isDarkMode ? {border:"groove"} : {}} onClick={changeTheme}> {isDarkMode ? "â˜€" : "ðŸŒ™"}{" "}</button>
      </div>    
     </div>
    </nav>
    <hr style={isDarkMode ? {display:"block"} : 
      {display: "none"}
    }></hr>
  </>
  )
}
export default Navbar
